$(document).ready(function(){
	   $('#neo div').hide();
	   $('#neo div:first').hide();
	   $('a').click(function(){    
	       var currentTab = '#'+$(this).text().toLowerCase();
	       if($(currentTab).is(':visible')){
	           $('#neo div').hide();
	           $(currentTab).hide();
	       }else{
	           $('#neo div').hide();
	           $(currentTab).show();
	       }
	       var gift = document.getElementById("neo");
		   	if(gift.src.match("getgift")){
		   		gift.src = "resources/images/emptygift.jpg"
		   	}else{
		   		gift.src="resources/images/getgift.jpg"
		   	}
	   }
	   );
});

function changeImage(){
	var gift = document.getElementById("giftimg");
	if(gift.src.match("getgift")){
		gift.src = "resources/images/emptygift.jpg"
	}else{
		gift.src="resources/images/getgift.jpg"
	}
}


